import React from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import Products from './components/Products';
import WhyChooseUs from './components/WhyChooseUs';
import Traction from './components/Traction';
import Contact from './components/Contact';
import Footer from './components/Footer';

function App() {
  // The navigateTo function now uses window.location.hash for smooth scrolling
  // This is a common pattern for single-page applications with sections.
  const navigateTo = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="app-container">
      {/* Header component, passing navigateTo for smooth scrolling */}
      <Header navigateTo={navigateTo} />

      <main className="main-content">
        {/* Each component now represents a section on the single page */}
        <Hero navigateTo={navigateTo} />
        <About />
        <Products />
        <WhyChooseUs />
        <Traction />
        <Contact />
      </main>

      {/* Footer component */}
      <Footer />
    </div>
  );
}

export default App;
